import React, { useState } from 'react';
import axios from 'axios';

const HoneycombPlotGenerator = () => {
  const [easy, setEasy] = useState(0);
  const [medium, setMedium] = useState(0);
  const [hard, setHard] = useState(0);
  const [plotUrl, setPlotUrl] = useState('');

  const generatePlot = async () => {
    try {
      const response = await axios.post('/generate_plot', { easy, medium, hard }, {
        responseType: 'blob',  // Important for handling binary data
      });

      const url = URL.createObjectURL(new Blob([response.data], { type: 'image/png' }));
      setPlotUrl(url);
    } catch (error) {
      console.error('Error generating plot:', error);
    }
  };

  return (
    <div >
      <h1>Generate Honeycomb Plot</h1>
      <form onSubmit={(e) => { e.preventDefault(); generatePlot(); }}>
        <label>
          Easy Questions:
          <input type="number" value={easy} onChange={(e) => setEasy(Number(e.target.value))} />
        </label>
        <label>
          Medium Questions:
          <input type="number" value={medium} onChange={(e) => setMedium(Number(e.target.value))} />
        </label>
        <label>
          Hard Questions:
          <input type="number" value={hard} onChange={(e) => setHard(Number(e.target.value))} />
        </label>
        <button type="submit">Generate Plot</button>
      </form>
      {plotUrl && <img src={plotUrl} alt="Honeycomb Plot" />}
    </div>
  );
};

export default HoneycombPlotGenerator;
