import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const HoneycombPlot = ({ easyCount, mediumCount, hardCount, width = 800, height = 600 }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    const createHoneycombPlot = () => {
      const totalCount = easyCount + mediumCount + hardCount;
      const questionTypes = [
        ...Array(easyCount).fill('easy'),
        ...Array(mediumCount).fill('medium'),
        ...Array(hardCount).fill('hard'),
      ];

      d3.shuffle(questionTypes);

      const colorMap = {
        easy: '#F9A500',
        medium: '#CD8DFF',
        hard: '#13A4E3',
      };

      const maxRadius = 20; // Starting radius
      const minRadius = 5;  // Minimum radius for the smallest hexagon

      const hexWidth = Math.sqrt(3) * maxRadius;
      const hexHeight = 2 * maxRadius;

      const directions = [
        [1, 0],    
        [-0.5, 0.75],   
        [-1, 0],  
        [0.5, -0.75]    
      ];

      const hexagonPoints = [];
      const visited = new Set();
      const startX = Math.random() * (width - hexWidth);
      const startY = Math.random() * (height - hexHeight);

      hexagonPoints.push([startX, startY]);
      visited.add(`${Math.round(startX)},${Math.round(startY)}`);

      while (hexagonPoints.length < totalCount) {
        const randomHexagon = hexagonPoints[Math.floor(Math.random() * hexagonPoints.length)];
        const [x, y] = randomHexagon;
        const randomDirection = directions[Math.floor(Math.random() * directions.length)];
        const newX = x + randomDirection[0] * hexWidth;
        const newY = y + randomDirection[1] * hexHeight;

        if (newX >= 0 && newX <= width - hexWidth &&
            newY >= 0 && newY <= height - hexHeight &&
            !visited.has(`${Math.round(newX)},${Math.round(newY)}`)) {
          hexagonPoints.push([newX, newY]);
          visited.add(`${Math.round(newX)},${Math.round(newY)}`);
        }
      }

      d3.select(svgRef.current).selectAll('*').remove();

      const svg = d3
        .select(svgRef.current)
        .attr('width', width)
        .attr('height', height);

      const createHexagonPath = (cx, cy, radius) => {
        const angle = Math.PI / 3;
        return d3.range(6).map(i => {
          const x = cx + radius * Math.cos(angle * i);
          const y = cy + radius * Math.sin(angle * i);
          return [x, y];
        });
      };

      hexagonPoints.forEach((pos, i) => {
        const radius = Math.max(minRadius, maxRadius - (i / totalCount) * (maxRadius - minRadius));
        const color = colorMap[questionTypes[i % questionTypes.length]];

        const hexagonPath = createHexagonPath(pos[0], pos[1], radius);

        svg
          .append('path')
          .attr('d', `M${hexagonPath.map(p => p.join(',')).join('L')}Z`)
          .attr('transform', `translate(${pos[0]}, ${pos[1]})`)
          .attr('fill', color)
          .attr('stroke', '#000')
          .attr('stroke-width', '1px');
      });

      // Adjust viewBox to ensure all hexagons are visible
      const minX = d3.min(hexagonPoints, d => d[0] - maxRadius);
      const maxX = d3.max(hexagonPoints, d => d[0] + maxRadius);
      const minY = d3.min(hexagonPoints, d => d[1] - maxRadius);
      const maxY = d3.max(hexagonPoints, d => d[1] + maxRadius);

      svg.attr('viewBox', `${minX} ${minY} ${maxX - minX} ${maxY - minY}`);
    };

    createHoneycombPlot();
  }, [easyCount, mediumCount, hardCount, width, height]);

  return (
    <div>
      <svg ref={svgRef}></svg>
    </div>
  );
};

export default HoneycombPlot;
